import Logo from "./Logo"

import React, { useState } from 'react';

export default function Header() {
  const [activeLink, setActiveLink] = useState('/');

  const handleLinkClick = (link) => {
    setActiveLink(link);
  };

  return (
    <header className="App-header">
      <Logo />
      <nav className="nav">
        <ul className="navList">
          <li className="navItem">
            <a
              href="/"
              className={`navLink ${activeLink === '/' ? 'active' : ''}`}
              onClick={() => handleLinkClick('/')}
            >
              Home
            </a>
          </li>
          <li className="navItem">
            <a
              href="/about-us"
              className={`navLink ${activeLink === '/about-us' ? 'active' : ''}`}
              onClick={() => handleLinkClick('/about-us')}
              style={
                {backgroundColor: activeLink === 'about-us' ? "" : "darkblue",
                color: activeLink === 'about-us' ? "white" : "darkblue"
            }
        }
            >
              About Us
            </a>
          </li>
          <li className="navItem">
            <a
              href="/contact"
              className={`navLink ${activeLink === '/contact' ? 'active' : ''}`}
              onClick={() => handleLinkClick('/contact')}
            >
              Contact
            </a>
          </li>
        </ul>
      </nav>
    </header>
  );
}
