import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Layout from "../components/Layout"
import Header from "../components/Header"
import Footer from "../components/Footer"
import "../styles/main.scss"
import DeleteItemButton from '../services/Delete';
import EditItemForm from '../services/EditItemForm'; // Import the EditItemForm component
import edit from "../images/pen.png"
import moment from 'moment';

export default function Home() {
  // ... (existing code remains the same)

  const handleEditItem = (itemId) => {
    const itemToEdit = items.find(item => item.id === itemId);
    setEditedItem(itemToEdit);
  };

  const handleSaveEdit = (updatedItem) => {
    const editedIndex = items.findIndex(item => item.id === updatedItem.id);
    const updatedItems = [...items];
    updatedItems[editedIndex] = updatedItem;
    setItems(updatedItems);
    setDisplayedItems(updatedItems.slice(0, 10));
    setEditedItem(null);
  };

  const handleCancelEdit = () => {
    setEditedItem(null);
  };

  return (
    <Layout>
      <Header />
  
      <div className="scrollable-box">
        <div className="item-grid">
          {displayedItems.map((item) => (
            <div key={item.id} className={`item ${editedItem && editedItem.id === item.id ? 'edit-mode' : ''}`}>
              <div className='item-header'>
                <span className="item-date">{item.date}</span>
                {editedItem && editedItem.id === item.id ? (
                  <>
                    <img src={edit} className='icon-edit' alt='Cancel' onClick={() => handleCancelEdit()} />
                    <DeleteItemButton onDelete={handleDeleteItem} itemId={item.id} />
                  </>
                ) : (
                  <>
                    <img src={edit} className='icon-edit' alt='Edit' onClick={() => handleEditItem(item.id)} />
                    <DeleteItemButton onDelete={handleDeleteItem} itemId={item.id} />
                  </>
                )}
              </div>
              {editedItem && editedItem.id === item.id ? (
                <EditItemForm
                  editedItem={editedItem}
                  onSaveEdit={handleSaveEdit}
                  onCancelEdit={handleCancelEdit}
                />
              ) : (
                <>
                  <h3>{item.title}</h3>
                  <p style={{ color: "rgb(80, 80, 139)" }}>{item.body}</p>
                </>
              )}
            </div>
          ))}
        </div>
        {displayedItems.length === 0 && <p style={{textAlign:"center", fontSize: "40px"}}>No articles in the box</p>}
      </div>
  
      <Footer />
    </Layout>
  );
}
