import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Layout from "../components/Layout"
import Header from "../components/Header"
import Footer from "../components/Footer"
import "../styles/main.scss"
import icon from "../images/remove.png"
import edit from "../images/pen.png"
import moment from 'moment';
import Delete from "../services/Delete"


export default function Home() {

  const [items, setItems] = useState([]);
  const [displayedItems, setDisplayedItems] = useState([]);
  const [editedItem, setEditedItem] = useState(null);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedBody, setEditedBody] = useState('');
  

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then((response) => response.json())
      .then((data) => {
        const mappedData = data.map(item => ({
          ...item,
          date: moment(item.date).format('LLLL')
        }));
        setItems(mappedData);
        setDisplayedItems(mappedData.slice(0, 10));
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  const handleDeleteItem = (itemId) => {
    Swal.fire({
      title: 'Are you sure?',
      text: 'You will not be able to recover this item!',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'Yes, delete it!',
    }).then((result) => {
      if (result.isConfirmed) {
        setItems(prevItems => prevItems.filter(item => item.id !== itemId));
        setDisplayedItems(prevDisplayedItems => prevDisplayedItems.filter(item => item.id !== itemId));

        Swal.fire('Deleted!', 'The item has been deleted.', 'success');
      }
    });
  };


  const handleEditItem = (itemId) => {
    const itemToEdit = items.find(item => item.id === itemId);
    setEditedItem(itemToEdit);
    setEditedTitle(itemToEdit.title);
    setEditedBody(itemToEdit.body);
  };

  const handleSaveEdit = () => {
    
    const editedIndex = items.findIndex(item => item.id === editedItem.id);

    
    const updatedItems = [...items];
    updatedItems[editedIndex] = {
      ...editedItem,
      title: editedTitle,
      body: editedBody,
    };

    
    setItems(updatedItems);
    setDisplayedItems(updatedItems.slice(0, 10));

    
    setEditedItem(null);
    setEditedTitle('');
    setEditedBody('');

    Swal.fire('Saved!', 'The changes have been saved.', 'success');
  };

  return (
    <Layout>
      <Header />
  
      <div className="scrollable-box">
        <div className="item-grid">
          {displayedItems.map((item) => (
            <div key={item.id} className={`item ${editedItem && editedItem.id === item.id ? 'edit-mode' : ''}`}>
              <div className='item-header'>
                <span className="item-date">{item.date}</span>
                {editedItem && editedItem.id === item.id ? (
                  <>
                    <img src={edit} className='icon-edit' alt='Cancel' onClick={() => {
                      setEditedItem(null);
                      setEditedTitle('');
                      setEditedBody('');
                    }} />
                    <img src={icon} className='icon-delete' alt="Delete" onClick={() => handleDeleteItem(item.id)} />
                  </>
                ) : (
                  <>
                    <img src={edit} className='icon-edit' alt='Edit' onClick={() => handleEditItem(item.id)} />
                    <img src={icon} className='icon-delete' alt="Delete" onClick={() => handleDeleteItem(item.id)} />
                  </>
                )}
              </div>
              {editedItem && editedItem.id === item.id ? (
                <div className="edit-form">
                  <input
                    type="text"
                    value={editedTitle}
                    onChange={(e) => setEditedTitle(e.target.value)}
                  />
                  <textarea
                    value={editedBody}
                    onChange={(e) => setEditedBody(e.target.value)}
                  />
                  <button onClick={handleSaveEdit}>Save</button>
                </div>
              ) : (
                <>
                  <h3>{item.title}</h3>
                  <p style={{ color: "rgb(80, 80, 139)" }}>{item.body}</p>
                </>
              )}
            </div>
          ))}
        </div>
        {displayedItems.length === 0 && <p>No articles in the box</p>}
      </div>
  
      <Footer />
    </Layout>
  );
              }
