import Layout from "../components/Layout"
import Header from "../components/Header"
import Footer from "../components/Footer"
import React, { useState } from 'react';
import Swal from 'sweetalert2';


export default function Home () {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [question, setQuestion] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();

    
    if (!name || !email || !question) {
      Swal.fire({
        icon: 'error',
        title: 'Validation error',
        text: 'Molim vas popunite sva polja forme.',
      });
      return;
    }

    if (!isValidEmail(email)) {
      Swal.fire({
        icon: 'error',
        title: 'Validation Error',
        text: 'Molim vas unesite validnu email adresu.',
      });
      return;
    }

    
    setSubmitted(true);
  };

  const isValidEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };



    return (
        <Layout>
            <Header />  

            <div className="contact-container">
                {submitted ? (
                    <div className="thank-you-message">
                        <h2>Hvala ti!</h2>
                        <p> Postovani {name}, hvala Vam sto se interesujete za nasu kompaniju.</p>
                        <p>Uspesno si poslao pitanje, ocekuj odgovor u sto kracem periodu na "{email}". </p>

                        
                        
                        
                    </div>
                ) : (
                    <form onSubmit={handleSubmit}>
                    <div className="contact-form">
                        <label>Name:</label>
                        <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        />
                    </div>
                    <div className="contact-form">
                        <label>Email:</label>
                        <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        />
                    </div>
                    <div className="contact-form">
                        <label>Question:</label>
                        <textarea
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        required
                        />
                    </div>
                    <button className="contact-button"type="submit">Submit</button>
                    </form>
                )}
            </div>




            <Footer />   
        </Layout>
    )
}
