import React, { useState, useEffect } from 'react';
import Layout from "../components/Layout"
import Header from "../components/Header"
import Footer from "../components/Footer"
import "../styles/main.scss"
import AddItemModal from '../services/AddItemModal';
import moment from 'moment';
import ScrolledBox from '../components/ScrolledBox';


export default function Home() {
    const [items, setItems] = useState([]);
    const [displayedItems, setDisplayedItems] = useState([]);
    const [showAddModal, setShowAddModal] = useState(false);
    
    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/posts')
        .then((response) => response.json())
        .then((data) => {
          const mappedData = data.map(item => ({
            ...item,
            date: moment(item.date).format('LLLL')
          }));
          setItems(mappedData);
          setDisplayedItems(mappedData.slice(0, 10));
        })
        .catch((error) => console.error('Error fetching data:', error));
    }, []);
  
    const handleAddItemClick = () => {
      setShowAddModal(true);
      };
  
      const handleAddItem = (newItem) => {
      setItems((prevItems) => [...prevItems, newItem]);
      setDisplayedItems((prevDisplayedItems) => [...prevDisplayedItems, newItem]);
      setShowAddModal(false);
      };
   

  return (
    <Layout>
      <Header />
      
      <ScrolledBox />
      {showAddModal && (
        <AddItemModal onAddItem={handleAddItem} onCancelAdd={ScrolledBox.handeCancelItem} />
      )}
      <button className="add-btn-empty" onClick={handleAddItemClick}>ADD ARTICLE</button>
      <Footer />

    </Layout>
  );
}
