import "../styles/login.scss"
import React from 'react';
import Swal from 'sweetalert2';
import { allowedUsers } from "../helper/AllowedUsers";
import {useEffect, useState} from "react";
import { useNavigate } from "react-router-dom";


export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false)

  const handleLogin = (e) => {
    e.preventDefault();

    const isUserAuthenticated = allowedUsers.filter((user, index) => {
        return user.email === email && user.password === password
    });

    if (isUserAuthenticated.length > 0) {
        localStorage.setItem("user", JSON.stringify(isUserAuthenticated[0]));
        const user = isUserAuthenticated[0]; 
      }

        
    }
useEffect(() => {
    if (localStorage.getItem("user")) {
        navigate("/home");
    }
}, []);

  
  

  return (
    <div className="login-container">
      <h2>Blue Grid Login</h2>
      <div className="form-group">
        <label>Username:</label>
        <input
          placeholder="Username.."
          type="text"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div className="form-group">
        <label>Password:</label>
        <input
          placeholder="Password.."
          type="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <button onClick={handleLoginSubmit}>Login</button>
    </div>
  );
}
