import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import moment from 'moment';
import '../styles/main.scss';

const ArticleDetails = ({ articles }) => {
  const { articleId } = useParams();
  const navigate = useNavigate(); // Add this line

  // Wait for the articles array to be available
  if (!articles || articles.length === 0) {
    return <div>Loading...</div>;
  }

  const article = articles.find((article) => article.id === parseInt(articleId));

  if (!article) {
    return <div>Article not found!</div>;
  }

  const handleGoBack = () => {
    navigate(-1); // Navigate back to the previous page
  };

  return (
    <div className="article-details-overlay">
      <div className="article-details-container">
        <button className="close-btn" onClick={handleGoBack}>
          &times;
        </button>
        <h2>{article.title}</h2>
        <p>{article.body}</p>
        <p>{moment(article.date).format('LLLL')}</p>
        <p>User ID: {article.userId}</p>
        <p>Article ID: {article.id}</p>
      </div>
    </div>
  );
};

export default ArticleDetails;
