import React, { useState } from 'react';
import Swal from 'sweetalert2';
import '../styles/login.scss';

export default function Login(){
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value.replace(/\s/g, '')); // Remove spaces
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value.replace(/\s/g, '')); // Remove spaces
  };

  const handleLogin = () => {
    
    if (!username.trim() || !password.trim()) {
        Swal.fire({
            icon: 'wrong',
            title: 'Error',
            text: 'Username or password cannot be empty!'
          });
    } 
     
      setLoggedIn(true);
      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: "Sucesfully logged in"
      });
    
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUsername('');
    setPassword('');
  };

  

  

  return (
    <div className="login-container">
      {loggedIn ? (
        <div>
          <h2>Welcome, {username}!</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <div className="form-group">
            <label>Username:</label>
            <input  placeholder= "Username.."type="text" value={username} onChange={handleUsernameChange} />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input placeholder='Password..' type="password" value={password} onChange={handlePasswordChange} />
          </div>
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};
