var exec = require('child_process').exec;

// --------------------------------------------------
// METHODS
// --------------------------------------------------

/**
 * Execução de comando em CMD/Terminal
 * @param command
 * @param callback
 */
function execCommand(command, callback) {
    exec(command, callback);
}

// --------------------------------------------------
// EXPORTS
// --------------------------------------------------

module.exports.run = execCommand;
