var string = require('./string');

// --------------------------------------------------
// METHODS
// --------------------------------------------------

/**
 * Retorno do erro de path não está vazio para a criação da aplicação
 * @returns {Error}
 * @constructor
 */
function simpleError(errorMessage, options) {
    return new Error(string.replaceMapper(errorMessage, options));
}

// --------------------------------------------------
// EXPORTS
// --------------------------------------------------

module.exports.simpleError = simpleError;
