// --------------------------------------------------
// METHODS
// --------------------------------------------------

/**
 * Realiza uma troca de chaves de um objeto passado por parâmetro por seus valores em uma String
 * @param string
 * @param object
 * @param startMap
 * @param endMap
 * @returns {*}
 */
function replaceMapper(string, object, startMap, endMap) {

    // Captura as chaves do objeto
    var keys = Object.keys(object);

    // Correção de start e end Map
    startMap = startMap ? startMap : '\<';
    endMap = endMap ? endMap : '>';

    // Percorre as chaves do objeto
    keys.forEach(function (key) {

        // Realiza a troca da chave em UpperCase pelo seu valor no objeto
        string = string.replace(startMap + key.toUpperCase() + endMap, object[key]);
    });

    // Retorna a String
    return string;
}

/**
 * Retorna uma string com o númeral adicionado de "0" (Zero) a esquerda
 * @param num
 * @param size
 * @returns {string}
 */
function pad(num, size) {
    var s = "000000000" + num;
    return s.substr(s.length - size);
}

// --------------------------------------------------
// EXPORTS
// --------------------------------------------------

module.exports.replaceMapper = replaceMapper;
module.exports.pad = pad;
